package main

import(
	"fmt"
	"os"
	"time"
	"strings"
)

func main() {
	start1 := time.Now();
	var s1,sep string;

	for i := 0; i < len(os.Args); i++ {
		s1 += sep + os.Args[i]
		sep = " | "
	}
	var time1 = time.Since(start1);
	fmt.Println();
	fmt.Print("elapsed time for loop : ");
	fmt.Println(time1);

	fmt.Print("\n\n");

	start2 := time.Now();
	// var s2 string;
	_ = strings.Join(os.Args," / ");

	var time2 = time.Since(start2);
	fmt.Print("elapsed time for join : ");
	fmt.Println(time2);

	fmt.Print("\n\n");
	// fmt.Println(s1);
	// fmt.Println(s2);


	//looping is slower than executing strings.join



}



